    # button_clicked Action
    def button_clicked(self, text):
        current_text = self.lineEdit.text()
        self.lineEdit.setText(current_text + text)

    def backspace(self):
        current_text = self.lineEdit.text()
        self.lineEdit.setText(current_text[:-1])

    def clear(self):
        self.lineEdit.setText("")

    def calculate(self):
        try:
            current_text = self.lineEdit.text()
            self.lineEdit.setText(str(eval(current_text)))
        except Exception as e:
            self.lineEdit.setText("Error")

    def keyPressEvent(self, event):
        key = event.key()
        if key == QtCore.Qt.Key_0:
            self.button_clicked("0")
        elif key == QtCore.Qt.Key_1:
            self.button_clicked("1")
        elif key == QtCore.Qt.Key_2:
            self.button_clicked("2")
        elif key == QtCore.Qt.Key_3:
            self.button_clicked("3")
        elif key == QtCore.Qt.Key_4:
            self.button_clicked("4")
        elif key == QtCore.Qt.Key_5:
            self.button_clicked("5")
        elif key == QtCore.Qt.Key_6:
            self.button_clicked("6")
        elif key == QtCore.Qt.Key_7:
            self.button_clicked("7")
        elif key == QtCore.Qt.Key_8:
            self.button_clicked("8")
        elif key == QtCore.Qt.Key_9:
            self.button_clicked("9")
        elif key == QtCore.Qt.Key_Period:
            self.button_clicked(".")
        elif key == QtCore.Qt.Key_Plus:
            self.button_clicked("+")
        elif key == QtCore.Qt.Key_Minus:
            self.button_clicked("-")
        elif key == QtCore.Qt.Key_Asterisk:
            self.button_clicked("*")
        elif key == QtCore.Qt.Key_Slash:
            self.button_clicked("/")
        elif key == QtCore.Qt.Key_ParenLeft:
            self.button_clicked("(")
        elif key == QtCore.Qt.Key_ParenRight:
            self.button_clicked(")")
        elif key == QtCore.Qt.Key_Enter or key == QtCore.Qt.Key_Return:
            self.calculate()
        elif key == QtCore.Qt.Key_Backspace:
            self.backspace()
        elif key == QtCore.Qt.Key_Escape:
            self.clear()


